module.exports.area_quadrado = (lado) => { return lado * lado }
module.exports.area_retangulo = (base, altura) => { return base * altura }
module.exports.area_triangola = (base, alutra) => { return (base * alutra) / 2 }
module.exports.area_circulo = (raio) => { const pi = 3.1416; return pi * raio * raio }